    margin-bottom: 20px;
    h3 {
      font-size: 28px;
    }
    button {
      width: 24px;
      height: 24px;
      background-color: transparent;
      border: none;
      text-align: center;
      cursor: pointer;
    }
  }
  .days-container {
    width: 350px;
    margin: auto;
    position: relative;
    .days {
